mapper.select:
select , s_user.nickname, s_user.avatar from s_information, s_user

	s_information.userid = s_user.userid and s_information.status != 9

		and s_information.status = #{status}


		and s_information.userid = #{userid}


		and s_information.important = #{important}


		and s_information.typeid = #{typeid}


order by s_information.createtime desc

select , s_user.nickname, s_user.avatar from s_information, s_order, s_orderdetail, s_user
and s_order.orderid = s_orderdetail.orderid and s_order.buyerid = s_user.userid and s_orderdetail.informationid = s_information.informationid and s_order.status != 9
and s_order.buyerid = #{buyerid}
and s_order.status = #{status}
order by s_order.createtime desc
