mapper.select:
select  from s_order 


		and status = #{status}


		and buyerid = #{buyerid}


		and sellerid = #{sellerid}

select s_order.* from s_order, s_orderdetail

	and s_order.orderid = s_orderdetail.orderid
	DESC
		and s_order.buyerid = #{buyerid}


		and s_orderdetail.informationid = #{informationid}
